import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import './PerfumeDetails.css';

const PerfumeDetails = () => {
  const { id } = useParams(); 
  const [perfume, setPerfume] = useState(null); 
  const [error, setError] = useState(null); 

  useEffect(() => {
    const fetchPerfume = async () => {
      try {
        const response = await axios.get(`http://localhost:8000/api/perfume/${id}`);
        setPerfume(response.data);
      } catch (error) {
        setError("Error loading perfume data.");
        console.error("Error loading perfume:", error);
      }
    };

    fetchPerfume();
  }, [id]);

  return (
    <>
      {error && <div className="error-message">{error}</div>} 
      {perfume ? (
        <div className="perfume-details-container">
          <div className="perfume-card">
            <img src={perfume.cardImage} alt={perfume.name} className="perfume-image" />
          </div>
          <div className="perfume-info">
            <h2>{perfume.name}</h2>
            <Link to={`/perfume/${perfume._id}`} className="view-details">View Details</Link>
            <h4>Price: ${perfume.price}</h4>

            {/* Dropdown for Available Sizes */}
            <label htmlFor="sizes">Available Sizes:</label>
            <select id="sizes" className="size-dropdown">
              {perfume.sizes.map((size, index) => (
                <option key={index} value={size}>{size}</option>
              ))}
            </select>

            {/* Client Reviews Link */}
            <Link to={`/reviews/${perfume._id}`} className="client-reviews">Client Reviews</Link>
          </div>
        </div>
      ) : (
        <p>Loading perfume details...</p>
      )}
    </>
  );
};

export default PerfumeDetails;
