import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './PerfumeDetails.css';

const PerfumeDetails = () => {
  const { id } = useParams();
  const [perfume, setPerfume] = useState(null);
  const [newReview, setNewReview] = useState('');
  const [gallery, setGallery] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchPerfume = async () => {
      try {
        const response = await axios.get(`http://localhost:8000/api/perfume/${id}`);
        const fetchedPerfume = response.data;

        setPerfume(fetchedPerfume);

        // Directly use the cardImage array
        const images = Array.isArray(fetchedPerfume.cardImage) ? fetchedPerfume.cardImage : [];
        
        setGallery(images);
      } catch (error) {
        setError("Error loading perfume data.");
        console.error("Error loading perfume:", error);
      }
    };

    fetchPerfume();
  }, [id]);

  const handleReviewSubmit = async () => {
    if (newReview) {
      try {
        const response = await axios.post(`http://localhost:8000/api/perfume/reviews/${id}`, { text: newReview });
        setPerfume((prevPerfume) => ({
          ...prevPerfume,
          reviews: [...(prevPerfume.reviews || []), response.data]
        }));
        setNewReview('');
      } catch (error) {
        console.error("Error submitting review:", error);
      }
    }
  };

  const handleShare = () => {
    const shareData = {
      title: perfume.name,
      text: `Check out this amazing perfume: ${perfume.name}!`,
      url: window.location.href,
    };

    if (navigator.share) {
      navigator.share(shareData).catch((error) => console.error('Error sharing:', error));
    } else {
      alert('Sharing is not supported on this browser.');
    }
  };

  return (
    <div className="perfume-details-page">
      {error && <div className="error-message">{error}</div>}
      {perfume ? (
        <div className="perfume-details-container">
          {/* Left Section - Gallery */}
          <div className="left-section">
            <h2 className="white-text">Gallery</h2>
            <div className="gallery-grid">
              {gallery.length > 0 ? (
                gallery.map((image, index) => (
                  <div className="gallery-card" key={index}>
                    <img 
                      src={image.trim()} 
                      alt={`Perfume Image ${index}`} 
                      className="gallery-image"
                      onError={(e) => e.target.src = '/default-image.jpg'}  // Fallback image if base64 fails
                    />
                  </div>
                ))
              ) : (
                <p className="white-text">No images available</p>
              )}
            </div>
          </div>

          {/* Right Section - Perfume Details */}
          <div className="right-section">
            <h2 className="white-text">{perfume.name}</h2>
            <h4 className="white-text">Price: ${perfume.price}</h4>
            
            {/* Sizes */}
            <div className="sizes">
              <strong className="white-text">Available Sizes:</strong>
              <ul className="white-text">
                {perfume.sizes && perfume.sizes.length > 0 ? (
                  perfume.sizes.map((size, index) => (
                    <li key={index}>{size}</li>
                  ))
                ) : (
                  <li>No sizes available</li>
                )}
              </ul>
            </div>

            {/* Reviews */}
            <div className="reviews">
              <h4 className="white-text">Reviews</h4>
              {perfume.reviews && perfume.reviews.length > 0 ? (
                <ul className="white-text">
                  {perfume.reviews.map((review, index) => (
                    <li key={index}>{review}</li>
                  ))}
                </ul>
              ) : (
                <p className="white-text">No reviews yet. Be the first to review!</p>
              )}

              {/* Add Review */}
              <div className="add-review">
                <textarea
                  value={newReview}
                  onChange={(e) => setNewReview(e.target.value)}
                  placeholder="Write your review here"
                  className="review-input"
                />
                <button className="btn-submit-review" onClick={handleReviewSubmit}>Submit Review</button>
              </div>
            </div>

            {/* Share Button */}
            <button className="btn-share" onClick={handleShare}>Share this Perfume</button>
          </div>
        </div>
      ) : (
        <p className="white-text">Loading perfume details...</p>
      )}
    </div>
  );
};

export default PerfumeDetails;
