import React from 'react';
import './PerfumeCards.css'; 
import perfume2 from '../Assets/perfume2.png';
import gallery5 from '../Assets/gallery5.png';
import gallery7 from '../Assets/gallery7.png';
import perfume1 from '../Assets/perfume1.png';
import perfume4 from '../Assets/perfume4.png';
import perfume6 from '../Assets/perfume6.png';

const PerfumeCards = () => {
  const perfumes = [
    {
      name: 'Aqua di Gioia',
      description: 'A refreshing blend of jasmine and rose.',
      price: '$70.99',
      image: perfume2
    },
    {
      name: 'Coco Chanel',
      description: 'Experience the coolness of the ocean in a bottle.',
      price: '$125.50',
      image: gallery5
    },
    {
      name: 'Seoul',
      description: 'A mysterious scent with hints of sandalwood.',
      price: '$55.75',
      image: gallery7
    },
    {
      name: 'Possess',
      description: 'A warm and cozy fragrance with vanilla undertones.',
      price: '$80.00',
      image: perfume1
    },
    {
      name: 'Dior',
      description: 'An enchanting scent with lavender and musk.',
      price: '$145.25',
      image: perfume4
    },
    {
      name: 'Givenchy Gentleman',
      description: 'A zesty blend of lemon and orange for a fresh start.',
      price: '$110.99',
      image: perfume6
    }
  ];

  return (
    <div className="perfume-showcase" style={{ backgroundImage: `url('Background.jpg')` }}>
      <div className="container py-5">
        <div className="row">
          {perfumes.map((perfume, index) => (
            <div className="col-md-4" key={index}>
              <div className="card perfume-card shadow-sm mb-4">
                <img src={perfume.image} className="card-img-top" alt={perfume.name} />
                <div className="card-body text-center">
                  <h5 className="card-title">{perfume.name}</h5>
                  <p className="card-text">{perfume.description}</p>
                  <h6 className="card-price">{perfume.price}</h6>
                  <button className="btn btn-details">View Details</button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default PerfumeCards;
