import React from 'react'; // Import React
import gi1 from '../Assets/gi1.png'; // Ensure the image path is correct
import './Homeimage.css'; // Import the CSS file for styling

const Homeimage = () => {
    return (
        // <div className="home-container">
        //     <div className="left-container"><h1>THE FREGRANCE OF LIFE</h1></div>
        //     <div><img src={gi1} alt="Perfume" className="home-image"  /></div>
        //     <div className="right-container"><p>Essenza di Luce" è un'esperienza olfattiva 
        //                                 unica che cattura l'essenza della freschezza e
        //                                  della femminilità. Le note di testa di 
        //                                 bergamotto e limone si fondono armoniosamente 
        //                                 con un cuore floreale di gelsomino e rosa, 
        //                                 creando una sinfonia di fragranze che evoca 
        //                                 un giardino in fiore.</p></div>
        // </div>
        <div className="d-flex" style={{ height: 'calc(100vh - 14vh)' }}>
        <div className="col-md-4 align-self-center">
        <h1 className="display-1 text-uppercase text-white">
        THE FREGRANCE OF LIFE
      </h1>
        </div>
        <div className="col-md-4 text-center">
        <img src={gi1} alt="Perfume" className="home-image"  />
        </div>
        <div className="col-md-4 align-self-end mb-5rem">
        <p className="para">
                                        Essenza di Luce" è un'esperienza olfattiva 
                                         unica che cattura l'essenza della freschezza e
                                          della femminilità. Le note di testa di 
                                         bergamotto e limone si fondono armoniosamente 
                                         con un cuore floreale di gelsomino e rosa, 
                                         creando una sinfonia di fragranze che evoca 
                                        un giardino in fiore.
        </p>
        </div>
        </div>
        
    );
}

export default Homeimage;
