import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './PerfumeDetails.css'; // Add necessary styles

const PerfumeDetails = () => {
  const { id } = useParams(); // Get perfume ID from URL
  
  const [perfume, setPerfume] = useState(null); // To hold perfume data
  const [newReview, setNewReview] = useState(''); // New review input
  const [gallery, setGallery] = useState([]); // To hold image gallery

  useEffect(() => {
    // Fetch perfume data by ID from the backend
    const fetchPerfume = async () => {
      try {
        const response = await axios.get(`/api/perfume/${id}`);
        const fetchedPerfume = response.data;

        setPerfume(fetchedPerfume);
        setGallery(fetchedPerfume.images); // Assuming the backend sends an array of image URLs
      } catch (error) {
        console.error("Error loading perfume:", error);
      }
    };

    fetchPerfume();
  }, [id]);

  // Handle new review submission
  const handleReviewSubmit = async () => {
    if (newReview) {
      try {
        const response = await axios.post(`/api/perfume/reviews/${id}`, { text: newReview });
        setPerfume((prevPerfume) => ({
          ...prevPerfume,
          reviews: [...prevPerfume.reviews, response.data] // Update reviews within perfume data
        }));
        setNewReview(''); // Clear input
      } catch (error) {
        console.error("Error submitting review:", error);
      }
    }
  };

  // Social media sharing function
  const handleShare = () => {
    const shareData = {
      title: perfume.name,
      text: `Check out this amazing perfume: ${perfume.name}!`,
      url: window.location.href,
    };

    if (navigator.share) {
      navigator.share(shareData).catch((error) => console.error('Error sharing:', error));
    } else {
      alert('Sharing is not supported on this browser.');
    }
  };

  return (
    perfume ? (
      <div className="perfume-details-container">
        <h2>{perfume.name}</h2>
        <p>{perfume.details}</p>
        <h4>Price: ${perfume.price}</h4>
        <div className="sizes">
          <strong>Available Sizes:</strong>
          <ul>
            {perfume.sizes.map((size, index) => (
              <li key={index}>{size}</li>
            ))}
          </ul>
        </div>

        {/* Gallery */}
        <div className="gallery">
          <h4>Gallery</h4>
          <div className="gallery-grid">
            {gallery.map((image, index) => (
              <img key={index} src={image} alt={`Perfume ${index}`} className="gallery-image" />
            ))}
          </div>
        </div>

        {/* Review Section */}
        <div className="reviews">
          <h4>Reviews</h4>
          {perfume.reviews && perfume.reviews.length > 0 ? (
            <ul>
              {perfume.reviews.map((review, index) => (
                <li key={index}>{review}</li> // If reviews are simple text, no need for .text
              ))}
            </ul>
          ) : (
            <p>No reviews yet. Be the first to review!</p>
          )}
          <div className="add-review">
            <textarea
              value={newReview}
              onChange={(e) => setNewReview(e.target.value)}
              placeholder="Write your review here"
            />
            <button onClick={handleReviewSubmit}>Submit Review</button>
          </div>
        </div>

        {/* Social Media Share Button */}
        <button className="btn-share" onClick={handleShare}>Share this Perfume</button>
      </div>
    ) : (
      <p>Loading perfume details...</p>
    )
  );
};

export default PerfumeDetails;
