import React, { useEffect, useState } from 'react';
import axios from 'axios'; // Import axios for API requests
import './PerfumeCards.css'; 

const PerfumeCards = () => {
  const [perfumes, setPerfumes] = useState([]); // State to hold perfume data
  const [loading, setLoading] = useState(true); // State to manage loading
  const [error, setError] = useState(null); // State to handle errors

  // Fetch data from the backend
  useEffect(() => {
    axios.get('http://localhost:8000/api/perfumes') // Replace with your API endpoint
      .then(response => {
        setPerfumes(response.data); // Set the perfumes data from the response
        setLoading(false); // Turn off loading
      })
      .catch(error => {
        setError(error.message); // Capture any errors
        setLoading(false); // Turn off loading
      });
  }, []); // Empty dependency array means it runs once after the component mounts

  if (loading) {
    return <div>Loading perfumes...</div>; // Show loading message
  }

  if (error) {
    return <div>Error loading perfumes: {error}</div>; // Show error message
  }

  return (
    <div className="perfume-showcase" style={{ backgroundImage: `url('Background.jpg')` }}>
      <div className="container py-5">
        <div className="row">
          {perfumes.map((perfume, index) => (
            <div className="col-md-4" key={index}>
              <div className="card perfume-card shadow-sm mb-4">
                <img 
                  src={perfume.cardImage} // Use the cardImage from backend
                  className="card-img-top" 
                  alt={perfume.name} 
                />
                <div className="card-body text-center">
                  <h5 className="card-title">{perfume.name}</h5>
                  <p className="card-text">{perfume.description}</p>
                  <h6 className="card-price">${perfume.price.toFixed(2)}</h6>
                  <button className="btn btn-details">View Details</button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default PerfumeCards;
