import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css'; 
import './PerfumeDetails.css'; 

const PerfumeDetails = () => {
  const { id } = useParams(); 
  const [perfume, setPerfume] = useState(null); 
  const [newReview, setNewReview] = useState(''); 
  const [gallery, setGallery] = useState([]); 
  const [error, setError] = useState(null); 

  useEffect(() => {
    const fetchPerfume = async () => {
      try {
        const response = await axios.get(`http://localhost:8000/api/perfume/${id}`);
        const fetchedPerfume = response.data;

        setPerfume(fetchedPerfume);

        // If `cardImage` is a string, split by commas to create an array
        const images = typeof fetchedPerfume.cardImage === 'string' 
          ? fetchedPerfume.cardImage.split(',') 
          : fetchedPerfume.cardImage || [];
        
        setGallery(images); // Set the processed gallery images
      } catch (error) {
        setError("Error loading perfume data.");
        console.error("Error loading perfume:", error);
      }
    };

    fetchPerfume();
  }, [id]);

  const handleReviewSubmit = async () => {
    if (newReview) {
      try {
        const response = await axios.post(`http://localhost:8000/api/perfume/reviews/${id}`, { text: newReview });
        setPerfume((prevPerfume) => ({
          ...prevPerfume,
          reviews: [...(prevPerfume.reviews || []), response.data]
        }));
        setNewReview('');
      } catch (error) {
        console.error("Error submitting review:", error);
      }
    }
  };

  const handleShare = () => {
    const shareData = {
      title: perfume.name,
      text: `Check out this amazing perfume: ${perfume.name}!`,
      url: window.location.href,
    };

    if (navigator.share) {
      navigator.share(shareData).catch((error) => console.error('Error sharing:', error));
    } else {
      alert('Sharing is not supported on this browser.');
    }
  };

  return (
    <>
      {error && <div className="alert alert-danger">{error}</div>} 
      {perfume ? (
        <div className="container mt-4">
          <div className="card">
            <div className="card-body">
              <h2 className="card-title">{perfume.name}</h2>
              <p className="card-text">{perfume.details}</p>
              <h4>Price: ${perfume.price}</h4>

              {/* Sizes */}
              <div className="sizes mb-3">
                <strong>Available Sizes:</strong>
                <ul className="list-group">
                  {perfume.sizes && perfume.sizes.length > 0 ? (
                    perfume.sizes.map((size, index) => (
                      <li className="list-group-item" key={index}>{size}</li>
                    ))
                  ) : (
                    <li className="list-group-item">No sizes available</li>
                  )}
                </ul>
              </div>

              {/* Gallery */}
              <h4 className="mt-4">Gallery</h4>
              <div className="row">
                {gallery.length > 0 ? (
                  gallery.map((image, index) => (
                    <div className="col-md-4 mb-3" key={index}>
                      <div className="card">
                        <img
                          src={image.trim()}
                          className="card-img-top"
                          alt={`Perfume Image ${index}`}
                        />
                      </div>
                    </div>
                  ))
                ) : (
                  <p>No images available</p>
                )}
              </div>

              {/* Reviews */}
              <div className="reviews mt-4">
                <h4>Reviews</h4>
                {perfume.reviews && perfume.reviews.length > 0 ? (
                  <ul className="list-group">
                    {perfume.reviews.map((review, index) => (
                      <li className="list-group-item" key={index}>{review}</li>
                    ))}
                  </ul>
                ) : (
                  <p>No reviews yet. Be the first to review!</p>
                )}
                <div className="mt-3">
                  <textarea
                    className="form-control"
                    value={newReview}
                    onChange={(e) => setNewReview(e.target.value)}
                    placeholder="Write your review here"
                  />
                  <button className="btn btn-primary mt-2" onClick={handleReviewSubmit}>Submit Review</button>
                </div>
              </div>

              {/* Social Media Share Button */}
              <button className="btn btn-success mt-4" onClick={handleShare}>Share this Perfume</button>
            </div>
          </div>
        </div>
      ) : (
        <p>Loading perfume details...</p>
      )}
    </>
  );
};

export default PerfumeDetails;
