import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './PerfumeDetails.css'; 

const PerfumeDetails = () => {
  const { id } = useParams(); 
  const [perfume, setPerfume] = useState(null); 
  const [newReview, setNewReview] = useState(''); 
  const [gallery, setGallery] = useState([]); 
  const [error, setError] = useState(null); 

  useEffect(() => {
    const fetchPerfume = async () => {
      try {
        const response = await axios.get(`http://localhost:8000/api/perfume/${id}`);
        const fetchedPerfume = response.data;

        setPerfume(fetchedPerfume);

        const images = typeof fetchedPerfume.cardImage === 'string' 
          ? fetchedPerfume.cardImage.split(',') 
          : fetchedPerfume.cardImage || [];
        
        setGallery(images);
      } catch (error) {
        setError("Error loading perfume data.");
        console.error("Error loading perfume:", error);
      }
    };

    fetchPerfume();
  }, [id]);

  const handleReviewSubmit = async () => {
    if (newReview) {
      try {
        const response = await axios.post(`http://localhost:8000/api/perfume/reviews/${id}`, { text: newReview });
        setPerfume((prevPerfume) => ({
          ...prevPerfume,
          reviews: [...(prevPerfume.reviews || []), response.data]
        }));
        setNewReview('');
      } catch (error) {
        console.error("Error submitting review:", error);
      }
    }
  };

  const handleShare = () => {
    const shareData = {
      title: perfume.name,
      text: `Check out this amazing perfume: ${perfume.name}!`,
      url: window.location.href,
    };

    if (navigator.share) {
      navigator.share(shareData).catch((error) => console.error('Error sharing:', error));
    } else {
      alert('Sharing is not supported on this browser.');
    }
  };

  return (
    <>
      {error && <div className="error-message">{error}</div>} 
      {perfume ? (
        <div className="perfume-details-container">
          <h2>{perfume.name}</h2>
          <p>{perfume.details}</p>
          <h4>Price: ${perfume.price}</h4>

          {/* Sizes */}
          <div className="sizes">
            <strong>Available Sizes:</strong>
            <ul>
              {perfume.sizes && perfume.sizes.length > 0 ? (
                perfume.sizes.map((size, index) => (
                  <li key={index}>{size}</li>
                ))
              ) : (
                <li>No sizes available</li>
              )}
            </ul>
          </div>

          {/* Gallery */}
          <div className="gallery">
            <h4>Gallery</h4>
            <div className="gallery-grid">
              {gallery.length > 0 ? (
                gallery.map((image, index) => (
                  <div className="card" key={index}>
                    <img src={image.trim()} alt={`Perfume Image ${index}`} className="card-img-top" />
                  </div>
                ))
              ) : (
                <p>No images available</p>
              )}
            </div>
          </div>

          {/* Reviews */}
          <div className="reviews">
            <h4>Reviews</h4>
            {perfume.reviews && perfume.reviews.length > 0 ? (
              <ul>
                {perfume.reviews.map((review, index) => (
                  <li key={index}>{review}</li>
                ))}
              </ul>
            ) : (
              <p>No reviews yet. Be the first to review!</p>
            )}
            <div className="add-review">
              <textarea
                value={newReview}
                onChange={(e) => setNewReview(e.target.value)}
                placeholder="Write your review here"
              />
              <button onClick={handleReviewSubmit}>Submit Review</button>
            </div>
          </div>

          {/* Social Media Share Button */}
          <button className="btn-share" onClick={handleShare}>Share this Perfume</button>
        </div>
      ) : (
        <p>Loading perfume details...</p>
      )}
    </>
  );
};

export default PerfumeDetails;
